st.markdown("""
<style>
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;700;900&family=Rajdhani:wght@300;400;600&display=swap');

:root {
    --bg-deep:      #020818;
    --bg-card:      #060f2e;
    --bg-card2:     #080d24;
    --neon-blue:    #00d4ff;
    --neon-green:   #00ff88;
    --neon-orange:  #ff6b35;
    --neon-purple:  #a855f7;
    --text-main:    #e2e8f0;
    --text-dim:     #94a3b8;
    --border-glow:  rgba(0, 212, 255, 0.3);
}

.stApp {
    background-color: var(--bg-deep);
    background-image:
        radial-gradient(ellipse at 20% 50%, rgba(0,212,255,0.04) 0%, transparent 50%),
        radial-gradient(ellipse at 80% 20%, rgba(168,85,247,0.04) 0%, transparent 50%),
        radial-gradient(ellipse at 60% 80%, rgba(0,255,136,0.03) 0%, transparent 40%);
    font-family: 'Rajdhani', sans-serif;
    color: var(--text-main);
}

#MainMenu, footer, header { visibility: hidden; }
.stDeployButton { display: none; }

[data-testid="stSidebar"] {
    background: linear-gradient(180deg, #060f2e 0%, #020818 100%);
    border-right: 1px solid var(--border-glow);
}
[data-testid="stSidebar"] * { color: var(--text-main) !important; }

.hero-banner {
    background: linear-gradient(135deg, #020818 0%, #060f2e 40%, #0a0a2e 100%);
    border: 1px solid rgba(0,212,255,0.2);
    border-radius: 16px;
    padding: 40px 48px;
    margin-bottom: 32px;
    animation: fadeInDown 0.8s ease;
}
@keyframes fadeInDown {
    from { opacity: 0; transform: translateY(-20px); }
    to   { opacity: 1; transform: translateY(0); }
}
@keyframes fadeInUp {
    from { opacity: 0; transform: translateY(16px); }
    to   { opacity: 1; transform: translateY(0); }
}

.hero-title {
    font-family: 'Orbitron', monospace;
    font-size: 2.2rem;
    font-weight: 900;
    background: linear-gradient(90deg, #00d4ff, #a855f7, #00ff88);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin: 0 0 8px 0;
}
.hero-sub {
    color: var(--text-dim);
    font-size: 1.05rem;
    letter-spacing: 2px;
    text-transform: uppercase;
}

.metric-row {
    display: flex;
    gap: 16px;
    margin-bottom: 28px;
    animation: fadeInUp 0.6s ease 0.2s both;
}
.metric-card {
    flex: 1;
    background: var(--bg-card);
    border: 1px solid rgba(0,212,255,0.15);
    border-radius: 12px;
    padding: 20px 24px;
    text-align: center;
    transition: border-color 0.3s, box-shadow 0.3s;
}
.metric-card:hover {
    border-color: var(--neon-blue);
    box-shadow: 0 0 20px rgba(0,212,255,0.15);
}
.metric-icon { font-size: 1.5rem; margin-bottom: 6px; }
.metric-value {
    font-family: 'Orbitron', monospace;
    font-size: 1.4rem;
    font-weight: 700;
    color: var(--neon-blue);
}
.metric-label {
    font-size: 0.76rem;
    color: var(--text-dim);
    text-transform: uppercase;
    letter-spacing: 1.5px;
    margin-top: 4px;
}

.glow-card {
    background: var(--bg-card);
    border: 1px solid rgba(0,212,255,0.12);
    border-radius: 14px;
    padding: 28px 32px;
    margin-bottom: 24px;
    transition: border-color 0.3s, box-shadow 0.3s;
}
.glow-card:hover {
    border-color: rgba(0,212,255,0.35);
    box-shadow: 0 0 30px rgba(0,212,255,0.08);
}
.glow-card-orange { border-color: rgba(255,107,53,0.15); }
.glow-card-orange:hover { border-color: rgba(255,107,53,0.4); box-shadow: 0 0 30px rgba(255,107,53,0.08); }
.glow-card-green  { border-color: rgba(0,255,136,0.12); }
.glow-card-green:hover  { border-color: rgba(0,255,136,0.4); box-shadow: 0 0 30px rgba(0,255,136,0.08); }
.glow-card-purple { border-color: rgba(168,85,247,0.12); }
.glow-card-purple:hover { border-color: rgba(168,85,247,0.4); box-shadow: 0 0 30px rgba(168,85,247,0.08); }

.section-heading {
    font-family: 'Orbitron', monospace;
    font-size: 1rem;
    font-weight: 700;
    color: var(--neon-blue);
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-bottom: 16px;
    display: flex;
    align-items: center;
    gap: 10px;
}
.section-heading::after {
    content: '';
    flex: 1;
    height: 1px;
    background: linear-gradient(90deg, rgba(0,212,255,0.3), transparent);
}

.info-box {
    background: rgba(0,212,255,0.05);
    border-left: 3px solid var(--neon-blue);
    border-radius: 0 8px 8px 0;
    padding: 12px 16px;
    margin: 12px 0;
    font-size: 0.95rem;
    color: var(--text-dim);
    line-height: 1.6;
}

.stTabs [data-baseweb="tab-list"] {
    background: var(--bg-card2);
    border-radius: 10px;
    padding: 4px;
    gap: 4px;
    border: 1px solid rgba(0,212,255,0.1);
}
.stTabs [data-baseweb="tab"] {
    background: transparent;
    border-radius: 8px;
    color: var(--text-dim) !important;
    font-family: 'Rajdhani', sans-serif;
    font-size: 0.95rem;
    font-weight: 600;
    letter-spacing: 1px;
    padding: 8px 20px;
    transition: all 0.2s;
}
.stTabs [aria-selected="true"] {
    background: rgba(0,212,255,0.12) !important;
    color: var(--neon-blue) !important;
    box-shadow: 0 0 12px rgba(0,212,255,0.1);
}

.sidebar-brand {
    font-family: 'Orbitron', monospace;
    font-size: 0.85rem;
    color: var(--neon-blue);
    text-align: center;
    padding: 12px 0 20px;
    letter-spacing: 2px;
    border-bottom: 1px solid rgba(0,212,255,0.15);
    margin-bottom: 20px;
}
</style>
""", unsafe_allow_html=True)

