with tab2:
    st.markdown('<div class="glow-card glow-card-orange">', unsafe_allow_html=True)
    st.markdown('<div class="section-heading">ðŸ”¬ Physics &#8212; Euler&#39;s Method</div>', unsafe_allow_html=True)
    st.markdown('<div class="info-box"><strong style="color:#ff6b35">Acceleration = (Thrust âˆ’ Gravity âˆ’ Drag) / Mass</strong><br>At every 1-second step, velocity and altitude update. As fuel burns, mass drops â†’ acceleration increases. Drag weakens with altitude as air thins.</div>', unsafe_allow_html=True)
    c1, c2, c3 = st.columns(3)
    thrust_kn = c1.slider("ðŸ”¥ Thrust (kN)",     500,  5000, 2500, step=100)
    payload_t = c2.slider("ðŸ“¦ Payload (tons)",     5,   100,   30, step=5)
    fuel_t    = c3.slider("â›½ Fuel Mass (tons)", 100,  1000,  400, step=50)
    st.markdown('</div>', unsafe_allow_html=True)

    @st.cache_data
    def run_simulation(thrust_kn, payload_t, fuel_t):
        thrust_N   = thrust_kn * 1000
        payload_kg = payload_t * 1000
        fuel_kg    = fuel_t    * 1000
        dry_mass   = 50_000; g = 9.81; drag_coeff = 0.47
        rho_0 = 1.225; area = 10.0; burn_rate = 50.0; dt = 1.0; steps = 600
        times, alts, vels, accels = [], [], [], []
        t, alt, vel = 0.0, 0.0, 0.0
        mass = dry_mass + payload_kg + fuel_kg
        for _ in range(steps):
            rho       = rho_0 * np.exp(-alt / 8500)
            drag      = 0.5 * rho * vel**2 * drag_coeff * area
            net_force = thrust_N - (mass * g) - drag
            accel     = net_force / mass
            vel      += accel * dt
            alt      += vel   * dt
            if mass > dry_mass + payload_kg:
                mass -= burn_rate * dt
            if alt < 0:
                alt = 0; vel = 0; break
            times.append(t); alts.append(alt / 1000)
            vels.append(vel); accels.append(accel)
            t += dt
        return pd.DataFrame({"Time (s)": times, "Altitude (km)": alts,
                              "Velocity (m/s)": vels, "Acceleration (m/sÂ²)": accels})

    with st.spinner("ðŸš€ Running simulation..."):
        sim = run_simulation(thrust_kn, payload_t, fuel_t)

    fig_sim = go.Figure()
