    fig_sim.add_trace(go.Scatter(x=sim["Time (s)"], y=sim["Acceleration (m/sÂ²)"] * 10,
        mode="lines", name="Acceleration Ã—10 (m/sÂ²)",
        line=dict(color="#00ff88", width=1.5, dash="dash")))
    fig_sim.update_layout(**PLOTLY_LAYOUT,
        title="ðŸš€ Rocket Launch Path â€” Altitude, Velocity & Acceleration",
        xaxis_title="Time (seconds)", yaxis_title="Value (scaled)",
        height=430, legend=dict(orientation="h", y=-0.2))
